$content = Get-Content '[id]\page.tsx'; $newContent = @(); $skip = 0; for ($i = 0; $i -lt $content.Count; $i++) { if ($content[$i] -match '<Code2 className="w-3 h-3" /> Source Code') { $skip = 3; continue } if ($skip -gt 0) { $skip--; if ($skip -eq 0 -and $content[$i] -match 'justify-between') { $content[$i-3] = $content[$i-3] -replace 'justify-between', 'justify-end' } continue } $newContent += $content[$i] } $newContent | Set-Content '[id]\page.tsx'